import { useState } from "react";
import styles from "./Calculator.module.css";

const Calculator = () => {
  const somarValores = () => {
    setResultado(valor1 + valor2);
  };

  const subtrairValores = () => {
    setResultado(valor1 - valor2);
  };

  const multiplicarValores = () => {
    setResultado(valor1 * valor2);
  };

  const dividirValores = () => {
    setResultado(valor1 / valor2);
  };

  const [valor1, setValor1] = useState(0);
  const [valor2, setValor2] = useState(0);
  const [resultado, setResultado] = useState(0);

  return (
    <div>
      <div className={styles.center}>
        <h1 className={styles.letra_titulo}>Calculadora Visual 1.0</h1>
      </div>
      {/* <fieldset className={styles.fieldset}> */}
      <legend>Minha Calculadora</legend>

      <div className={styles.grid_container}>
        <div className={styles.grid_item.item1}>
          <input
            className={styles.input}
            type="text"
            id="num1"
            onChange={(e) => setValor1(parseInt(e.target.value))}
            value={valor1}
          />
        </div>
        <div className={styles.grid_item.item2}>
          <input
            className={styles.input}
            type="text"
            id="num2"
            onChange={(e) => setValor2(parseInt(e.target.value))}
            value={valor2}
          />
        </div>

        <div className={styles.grid_item.item3}>
          <button className={styles.button_azul} onClick={somarValores}>
            +
          </button>
          <button className={styles.button_amarelo} onClick={subtrairValores}>
            -
          </button>
        </div>
        <div className={styles.grid_item.item4}>
          <button className={styles.button_verde} onClick={multiplicarValores}>
            x
          </button>
          <button className={styles.button_vermelho} onClick={dividirValores}>
            ÷
          </button>
        </div>

        <div className={styles.grid_item.item5}>
          <label className={styles.label}>=</label>
        </div>

        <div className={styles.grid_item.item6}>
          <input
            className={styles.input2}
            type="text"
            id="resultado"
            value={resultado}
            readOnly
          />
        </div>
      </div>
      {/* </fieldset> */}
    </div>
  );
};

export default Calculator;
