import styles from "./Calculator.module.css";

const Calculator = () => {
  const somarValores = () => {
    var n1 = parseInt(document.getElementById("num1").value);
    var n2 = parseInt(document.getElementById("num2").value);
    var n3 = n1 + n2;
    document.getElementById("resultado").value = n3;
  };

  const subtrairValores = () => {
    var n1 = parseInt(document.getElementById("num1").value);
    var n2 = parseInt(document.getElementById("num2").value);
    var n3 = n1 - n2;
    document.getElementById("resultado").value = n3;
  };

  const multiplicarValores = () => {
    var n1 = parseInt(document.getElementById("num1").value);
    var n2 = parseInt(document.getElementById("num2").value);
    var n3 = n1 * n2;
    document.getElementById("resultado").value = n3;
  };

  const dividirValores = () => {
    var n1 = parseInt(document.getElementById("num1").value);
    var n2 = parseInt(document.getElementById("num2").value);
    var n3 = n1 / n2;
    document.getElementById("resultado").value = n3;
  };

  return (
    <div>
      <div className={styles.center}>
        <h1 className={styles.letra_titulo}>Calculadora Visual 1.0</h1>
      </div>
      <fieldset className={styles.fieldset}>
        <legend>Minha Calculadora</legend>

        <div className={styles.grid_container}>
          <div className={styles.grid_item.item1}>
            <input className={styles.input} id="num1" type="text" />
          </div>
          <div className={styles.grid_item.item2}>
            <input className={styles.input} id="num2" type="text" />
          </div>

          <div className={styles.grid_item.item3}>
            <button className={styles.button_azul} onClick={somarValores}>
              +
            </button>
            <button
              className={styles.button_amarelo}
              onClick={subtrairValores()}
            >
              -
            </button>
          </div>
          <div className={styles.grid_item.item4}>
            <button
              className={styles.button_verde}
              onClick={multiplicarValores}
            >
              x
            </button>
            <button
              className={styles.button_vermelho}
              onClick={dividirValores()}
            >
              ÷
            </button>
          </div>

          <div className={styles.grid_item.item5}>
            <label className={styles.label} for="igual">
              =
            </label>
          </div>

          <div className={styles.grid_item.item6}>
            <input className={styles.input2} id="resultado" type="text" />
          </div>
        </div>
      </fieldset>
    </div>
  );
};

export default Calculator;
